import React, { useEffect, useState } from 'react'
import moment from 'moment/moment';
import 'chartjs-adapter-date-fns';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    TimeScale
} from 'chart.js';
import { Line } from 'react-chartjs-2';

import { 
  SunlightFormat,
  MoistureFormat,
  ReservoirFormat
} from '../../Constants';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);



const MinuteChart = (props) => {
  const [labels, setLabels] = useState([]);
  const [moisture, setMoisture] = useState([]);
  const [sunlight, setSunlight] = useState([]);
  const [maxY, setMaxY] = useState(0);
  const [minY, setMinY] = useState(0);

  const generateMinutes = () => {
    const items = [];
    new Array(24).fill().forEach((acc, index) => {
      items.push(moment( {hour: index} ).format('h:mm A'));
      items.push(moment({ hour: index, minute: 15 }).format('h:mm A'));  
      items.push(moment({ hour: index, minute: 30 }).format('h:mm A'));  
      items.push(moment({ hour: index, minute: 45 }).format('h:mm A'));  
    })
    return items;
  }
  
  const processData = (data) => {
    const moisture = [];
    const sunlight = [];
    const today = moment().format('YYYY-MM-DD');
    data.data.map((item) => {
      const date = item[0].split(" ")[0]
      const minute = item[0].split(" ")[1].split(":")[1]
      if (date == today && (minute == '00' || minute == '30'|| minute == '15') || minute == '45') {
        moisture.push(MoistureFormat(JSON.parse(item[1])));
        sunlight.push(SunlightFormat(JSON.parse(item[4])));
      }
    })
    setMoisture(moisture.reverse());
    setSunlight(sunlight.reverse());
    setMaxY(Math.floor(Math.max(...moisture, ...sunlight)));
    setMinY(Math.floor(Math.min(...moisture, ...sunlight)));

  }
  const options = {
    responsive: true,
    interaction: {
        mode: 'index',
        intersect: false,
    },
    stacked: false,
    plugins: {
        title: {
            display: true,
            text: 'Moisture Level vs Sunlight Level (30 Minutes)',
        },
    },
    scales: {
      x: {
        ticks: {
          autoSkip: true,
          maxTicksLimit: 24,
        },
      },
        y: {
            type: 'linear',
            display: true,
            position: 'left',
            min: minY-5,
            max: maxY+5,
            stepSize: 1,
            title: {
              display: true,
              text: 'Moisture Level %'
            }
        },
        y1: {
            type: 'linear',
            display: true,
            position: 'right',
            min: minY-5,
            max: maxY+5,
            stepSize: 1,
            grid: {
                drawOnChartArea: false,
            },
            title: {
              display: true,
              text: 'Sunlight Level'
            }
        },
    },
  };

  const chartdata = {
    labels,
    datasets: [
      {
        label: 'Moisture Level %',
        data: moisture,
        borderColor: 'rgb(255, 99, 132)',
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
        yAxisID: 'y',
      },
      {
        label: 'Sunlight Level',
        data: sunlight,
        borderColor: 'rgb(53, 162, 235)',
        backgroundColor: 'rgba(53, 162, 235, 0.5)',
        yAxisID: 'y1',
      },
    ],
  };

  const [data, setData] = useState([]);

  const getData = async () => {

    try {
      const res = await fetch("http://192.168.68.63:8088/getalldata")
      const result = await res.json()
      setData(result)
      setError(false)
    } catch (err) {
      setError(true)
    }

  }

  useEffect(() => {
    getData();
    // calls api every 1 minute
    setInterval(getData, 1 * 60 * 1000);
  }, [])
  const [error, setError] = useState(false);

  useEffect(() => {
    setLabels(generateMinutes());
    getData(props);
}, [props])
    return (
        <Line options={options} data={chartdata} />
    )
}

export default MinuteChart
