import React from 'react'
import { useEffect, useState } from 'react'
import HourlyChart from './Charts/HourlyChart';
import LiveChart from './Charts/LiveChart';

const LiveData = () => {

    const [data, setData] = useState({
        temp: 0.0,
        humidity: 0.0,
        timestamp: new Date().getTime(),
        moisture: 0.0,
        sunlight: 0.0,
        reservoir: 0.0
    });
    const [error, setError] = useState(false);

    const loadLiveData = async () => {
        try {
            const res = await fetch("http://192.168.68.63:8089/getlivedata")
            const result = await res.json()
            console.log(result);
            setData({
                temp: result.temperature,
                humidity: result.humidity,
                timestamp: result.time,
                moisture: result.moisture,
                sunlight: result.sunlight,
                reservoir: result.reservoir
            })
            setError(false)
        } catch (err) {
            setError(true)
        }
    }
    useEffect(() => {
        setInterval(loadLiveData, 2000);
    }, [])

    useEffect(() => {
        if (buttonStatus === true) {
          const socket = io("localhost:5001/", {
            transports: ["websocket"],
            cors: {
              origin: "http://localhost:3000/",
            },
          });
    
          setSocketInstance(socket);
    
          socket.on("connect", (data) => {
            console.log(data);
          });
    
          setLoading(false);
    
          socket.on("disconnect", (data) => {
            console.log(data);
          });
    
          return function cleanup() {
            socket.disconnect();
          };
        }
      }, [buttonStatus]);
      
    return (
        <div>
            {error ? <div >
                <h3>ERROR &rarr;</h3>
                <p>Could not connect to server</p>
            </div> :
                <>
                    <div >
                        <h3>Moisture &rarr;</h3>
                        <p>{Math.round(data.moisture)} %</p>
                    </div>
                    <div >
                        <h3>Temperature &rarr;</h3>
                        <p>{Math.round(data.temp)} Â°C</p>
                    </div>
                    <div >
                        <h3>Humidity &rarr;</h3>
                        <p>{Math.round(data.humidity)} %</p>
                    </div>
                    <div >
                        <h3>Sunlight &rarr;</h3>
                        <p>{Math.round(data.sunlight)} %</p>
                    </div>
                    <div >
                        <h3>Reservoir &rarr;</h3>
                        <p>{data.reservoir}</p>
                    </div>
                    <div >
                        <h3>Timestamp &rarr;</h3>
                        <p>{data.timestamp}</p>
                    </div>
                    <div>
                    <LiveChart></LiveChart>
                    </div>
                    

                </>

            }
        </div>
    )
}

export default LiveData
