import React, { useEffect, useState } from 'react'
import moment from 'moment/moment';
import 'chartjs-adapter-date-fns';
import ChartStreaming from 'chartjs-plugin-streaming';

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
} from 'chart.js';
import { Line } from 'react-chartjs-2';


ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
  ChartStreaming
);


const LiveChart = (props) => {
  const [labels, setLabels] = useState([]);
  const [moisture, setMoisture] = useState([]);
  const [sunlight, setSunlight] = useState([]);
  const [load, setLoad] = useState(false);
  const [data, setData] = useState([]);

  const generateMinutes = () => {
    const items = [];
    new Array(24).fill().forEach((acc, index) => {
      items.push(moment({ hour: index }).format('h:mm A'));
      items.push(moment({ hour: index, minute: 15 }).format('h:mm A'));
      items.push(moment({ hour: index, minute: 30 }).format('h:mm A'));
      items.push(moment({ hour: index, minute: 45 }).format('h:mm A'));
    })
    return items;
  }

  const getData = (data) => {
    // console.log(data.data.moisture);
    // setData(data.data.moisture);
  }


  const options = {
    responsive: true,
    interaction: {
      mode: 'index',
      intersect: false,
    },
    stacked: false,
    plugins: {
      title: {
        display: true,
        text: 'Moisture Level vs Sunlight Level (30 Minutes)',
      },
    },
    scales: {
      x: {
        type: 'realtime',
        realtime: {
          onRefresh: chart => {
            fetch("http://192.168.68.63:8089/getlivedata")
              .then(res => res.json())
              .then(
                (result) => {
                  var currentData = {
                    x: Date.now(),
                    y: result.moisture
                  };
                  var temp = chart.data.datasets[0].data;
                  console.log(temp);
                  temp.push(currentData)
                  chart.data.datasets[0].data.push(temp);
                  console.log(chart.data.datasets[0].data);
                  chart.update();
                });
          }
        }
      },
      y: {
        type: 'linear',
        display: true,
        position: 'left',
        min: 0,
        max: 100,
        stepSize: 1,

      },
    },
  };

  const chartdata = {
    labels,
    datasets: [
      {
        data: [0],
        label: 'Moisture Level %',
        borderColor: 'rgb(255, 99, 132)',
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
        yAxisID: 'y',
      },
    ],
  };
  useEffect(() => {
    setLabels(generateMinutes());
    setLoad(true);
    getData(props);
  }, [props])
  return (
    <div>

      {/* <Line options={options} data={chartdata} /> */}

    </div>
  )
}

export default LiveChart
