import logo from './logo.svg';
import './App.css';
import LiveData from './components/LiveData';
import ChartData from './components/ChartData';
import { useEffect, useState } from 'react'
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import 'bootstrap/dist/css/bootstrap.min.css';
import { ReservoirFormat } from './Constants';

function App() {

  const [reservoir, setReservoir] = useState(0.0);

  const getReservoir = (data) => {
    setReservoir(data)
  }
  return (
    <div className="App">
      <Container>
        <Row>
          <Col sm={8} className="border">
            <div className='livechart'>
              <LiveData getReservoir={getReservoir} />
            </div>
          </Col>
          <Col className="border">
            <div className='liveDataBox sunlight'>
              <h3 className='liveDataLabel'>Sunlight</h3>
              <div className='liveDataReading'>{ReservoirFormat(reservoir)} <span className='suffix'>%</span></div>
            </div>
          </Col>
        </Row>
        <Row>
          <Col sm={8} className="border">
            <div className='chartdata'>
              <ChartData />
            </div>
          </Col>
          <Col className="border">
            <div>
              <h1>Settings</h1>
            </div>
          </Col>
        </Row>
      </Container>

    </div>
  );
}

export default App;
