import React, { useState } from 'react'
import HourlyChart from './Charts/HourlyChart';
import MinuteChart from './Charts/MinuteChart';

const ChartData = () => {
  const [duration, setTimeType] = useState('mins');
  const [chartType, setChartType] = useState('mins');
  // write a case statement to determine which chart to render
  
  return (
    <div>
      <button onClick={()=>{setChartType('hour')}}>HOUR</button>
      <button onClick={()=>{setChartType('mins')}}>MINUTES</button>

      {chartType == 'hour' ? <HourlyChart/> : <MinuteChart/>}
    </div>
  )
}

export default ChartData
