const sunlight_analog_max = 255
const moisture_analog_dry = 230 // 230 is 100% dry
const moisture_analog_wet = 200 // 200 is 100% wet
const reservour_analog_max = 120 // 120 is 100% full
export function SunlightFormat(input) {
    return (((Math.abs(input - 255)) / 255) * 100).toFixed(0);
}

export function MoistureFormat(input) {
    var output = (Math.abs(input - moisture_analog_dry)) / 30
    if (output > 1) {
        output = 1
    }
    return (output * 100).toFixed(0);
}
// 120 is 100% full and 0 is 100% empty
export function ReservoirFormat(input) {
    return ((input/120) * 100).toFixed(0);
}
