import React, { useState } from 'react'
import HourlyChart from './Charts/HourlyChart';
import MinuteChart from './Charts/MinuteChart';

const ChartData = () => {
  const [duration, setDuration] = useState('mins');
  const [chartType, setChartType] = useState('MS');
  // write a case statement to determine which chart to render
  
  return (
    <div>
      <button onClick={()=>{setDuration('hour')}}>HOUR</button>
      <button onClick={()=>{setDuration('mins')}}>MINUTES</button>
      <button onClick={()=>{setChartType('MS')}}>Moisture Sunlight</button>
      <button onClick={()=>{setChartType('TH')}}>Temp Humidity</button>
      <button onClick={()=>{setChartType('R')}}>Reservoir</button>
      
      {
        duration == 'hour' ? 
          chartType == 'MS' ? 
          <HourlyChart type='MS'/> 
          : chartType == 'TH' ? 
          <HourlyChart type='TH'/>
          : <HourlyChart type='R'/>
        : chartType == 'MS' ? 
          <MinuteChart type='MS'/> 
          : chartType == 'TH' ? 
          <MinuteChart type='TH'/>
          : <MinuteChart type='R'/>
      }
    </div>
  )
}

export default ChartData
