import React from 'react'
import { useEffect, useState } from 'react'
import HourlyChart from './Charts/HourlyChart';
import LiveChart from './Charts/LiveChart';
import { io } from "socket.io-client";

const LiveData = () => {

    const [data, setData] = useState({
        temp: 0.0,
        humidity: 0.0,
        timestamp: new Date().getTime(),
        moisture: 0.0,
        sunlight: 0.0,
        reservoir: 0.0
    });
    const [error, setError] = useState(false);

    const loadLiveData = async () => {

        try {
            const res = await fetch("http://192.168.68.63:8089/getlivedata")
            const result = await res.json()
            console.log(result);
            setData({
                temp: result.temperature,
                humidity: result.humidity,
                time: result.time,
                moisture: result.moisture,
                sunlight: result.sunlight,
                reservoir: result.reservoir
            })
            setError(false)
        } catch (err) {
            setError(true)
        }
    }
    useEffect(() => {

        fetch("http://192.168.68.63:8089/getdata")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result.items
            });
            },
        )

        // establish connection with server
        const socket = io("http://192.168.68.63:8089/", {
          transports: ["websocket"],
          cors: {
            origin: "http://localhost:3000/",
          },
        });
    
        socket.on("connect", (data) => {
            console.log(data);
          
        });
    
        socket.on("disconnect", (data) => {
          console.log(data);
        });
    
        setInterval(() => {
            socket.emit("live_data")
        }, 3000);

        socket.on("live_data", (data) => {
            console.log(data);
            var getData = JSON.parse(data.data);
            setData({
                temp: getData.temperature,
                humidity: getData.humidity,
                moisture: getData.moisture,
                sunlight: getData.sunlight,
                reservoir: getData.reservoir,
                timestamp: getData.time

            });
        });

        return function cleanup() {
          socket.disconnect();
        };

      }, []);
    
    return (
        <div>
            {error ? <div >
                <h3>ERROR &rarr;</h3>
                <p>Could not connect to server</p>
            </div> :
                <>
                    <div >
                        <h3>Moisture &rarr;</h3>
                        <p>{data.moisture} %</p>
                    </div>
                    <div >
                        <h3>Temperature &rarr;</h3>
                        <p>{data.moisture} Â°C</p>
                    </div>
                    <div >
                        <h3>Humidity &rarr;</h3>
                        <p>{data.humidity} %</p>
                    </div>
                    <div >
                        <h3>Sunlight &rarr;</h3>
                        <p>{data.sunlight} %</p>
                    </div>
                    <div >
                        <h3>Reservoir &rarr;</h3>
                        <p>{data.reservoir}</p>
                    </div>
                    <div >
                        <h3>Timestamp &rarr;</h3>
                        <p>{data.timestamp}</p>
                    </div>
                    <div>
                        <LiveChart></LiveChart>
                    </div>


                </>

            }
        </div>
    )
}

export default LiveData
