import React from 'react'
import { useEffect, useState } from 'react'
import HourlyChart from './Charts/HourlyChart';
import LiveChart from './Charts/LiveChart';
import { io } from "socket.io-client";

const LiveData = ({ socket }) => {

    const [data, setData] = useState({
        temp: 0.0,
        humidity: 0.0,
        timestamp: new Date().getTime(),
        moisture: 0.0,
        sunlight: 0.0,
        reservoir: 0.0
    });
    const [error, setError] = useState(false);

    const loadLiveData = async () => {

        try {
            const res = await fetch("http://192.168.68.63:8089/getlivedata")
            const result = await res.json()
            console.log(result);
            setData({
                temp: result.temperature,
                humidity: result.humidity,
                time: result.time,
                moisture: result.moisture,
                sunlight: result.sunlight,
                reservoir: result.reservoir
            })
            setError(false)
        } catch (err) {
            setError(true)
        }
    }

    useEffect(() => {
        // emit1()
        // socket.on("data", (data) => {
        //     console.log(data);
        //   });

        //     
        // });

        
    }, [socket]);

    useEffect(() => {
        if (socket.connected) {
            console.log("sa;ldkf");
        socket?.on("data", (data) => {
            console.log(data);
            var getData = JSON.parse(data.data);
            setData({
                temp: getData.temperature,
                humidity: getData.humidity,
                moisture: getData.moisture,
                sunlight: getData.sunlight,
                reservoir: getData.reservoir,
                timestamp: getData.time
    
            });
        });
    }
    });

    return (
        <div>
            {error ? <div >
                <h3>ERROR &rarr;</h3>
                <p>Could not connect to server</p>
            </div> :
                <>
                    <div >
                        <h3>Moisture &rarr;</h3>
                        <p>{data.moisture} %</p>
                    </div>
                    <div >
                        <h3>Temperature &rarr;</h3>
                        <p>{data.moisture} °C</p>
                    </div>
                    <div >
                        <h3>Humidity &rarr;</h3>
                        <p>{data.humidity} %</p>
                    </div>
                    <div >
                        <h3>Sunlight &rarr;</h3>
                        <p>{data.sunlight} %</p>
                    </div>
                    <div >
                        <h3>Reservoir &rarr;</h3>
                        <p>{data.reservoir}</p>
                    </div>
                    <div >
                        <h3>Timestamp &rarr;</h3>
                        <p>{data.timestamp}</p>
                    </div>
                    <div>
                        <LiveChart data={data}></LiveChart>
                    </div>


                </>

            }
        </div>
    )
}

export default LiveData
