import React, { useEffect, useState } from 'react'
import moment from 'moment/moment';
import 'chartjs-adapter-date-fns';
import { ChartStreaming, RealTimeScale } from 'chartjs-plugin-streaming';

// import {
//   Chart as ChartJS,
//   CategoryScale,
//   LinearScale,
//   PointElement,
//   LineElement,
//   Title,
//   Tooltip,
//   Legend,
//   TimeScale,
// } from 'chart.js';
import { Line } from 'react-chartjs-2';


ChartJS.register(
  // CategoryScale,
  // LinearScale,
  // PointElement,
  // LineElement,
  // Title,
  // Tooltip,
  // Legend,
  // TimeScale,
  ChartStreaming
);


const LiveChart = (props) => {
  const [data, setData] = useState([]);

  const options = {
    scales: {
      x: {
        type: 'realtime',
        realtime: {
          onRefresh: chart => {
            var current = data;
            current.push({
                  x: Date.now(),
                  y: Math.random()
            })
            setData(current)
            chart.data.datasets[0].data = current;
            console.log(data.length);
            chart.update('quiet')
          }
        }
      },
      y: {
        // type: 'linear',
        // display: true,
        // position: 'left',
        // min: 0,
        max: 1,
        // stepSize: 1,
        beginAtZero: true,
      },
    },
  };

  const chartdata = {
    datasets: [
      {
        data: [],
        label: 'Moisture Level %',
        borderColor: 'rgb(255, 99, 132)',
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
        yAxisID: 'y',
      },
    ],
  };

  return (
    <div>
      <Line options={options} data={chartdata} />
    </div>
  )
}

export default LiveChart
